import React,{Component} from 'react';
 
class Greet extends Component{
    
        constructor(){
            super();
            this.state = {
            message : 'Subscribe',
            color : ''
            }
        }
        buttonClick(){
            this.setState({
                message : 'Subscribed'
            })
        }
        textChange = (event) => {
            this.setState({
                color : event.target.value
            })
        }
        bgColor = {
            backgroundColor : this.state.color
        }
        
    render(props){
        return(
            <div style={bgcolor}>
                <h1>Hello {this.props.name} <br/> Nick name is {this.props.nick}</h1>
                <input type="text" value={this.state.color} onChange={this.textChange}/>
                <button onClick={()=>this.buttonClick()}>{this.state.message}</button>
            </div>
        )
    }
}

export default Greet
