import React,{Component} from 'react';
import './style.css';
 
class Greet extends Component{
    
        constructor(){
            super();
            this.state = {
            message : 'Subscribe',
            color : ''
            }
        }
        buttonClick(){
            this.setState({
                message : 'Subscribed'
            })
        }
        textChange = (event) => {
            this.setState({
                color : event.target.value
                })
        }
        
        
    render(props){
        return(
            <div className="work">
                <h1>Hello {this.props.name} <br/> Nick name is {this.state.color}</h1>
                <input type="text" value={this.state.color} onChange={this.textChange} placeholder="Give Nicknames.."/>
                {/* <h2>{this.state.color}</h2> */}
                <button onClick={()=>this.buttonClick()}>{this.state.message}</button>
            </div>
        )
    }
}

export default Greet
