import React, {Component} from 'react';

class Calci extends Component{
    constructor(){
        super();
            this.state = {
                temp : '',
                firstOprand :  '0',
                secondOprand : '',
                Operator : ''
        }
    }
    seven(){
        if(this.state.temp===''){
            this.setState({
                temp : '7'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '7'
            })
        }
    }
    eight(){
        if(this.state.temp===''){
            this.setState({
                temp : '8'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '8'
            })
        }
    }
    nine(){
        if(this.state.temp===''){
            this.setState({
                temp : '9'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '9'
            })
        }
    }
    four(){
        if(this.state.temp===''){
            this.setState({
                temp : '4'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '4'
            })
        }
    }
    five(){
        if(this.state.temp===''){
            this.setState({
                temp : '5'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '5'
            })
        }
    }
    six(){
        if(this.state.temp===''){
            this.setState({
                temp : '6'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '6'
            })
        }
    }
    one(){
        if(this.state.temp===''){
            this.setState({
                temp : '1'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '1'
            })
        }
    }
    two(){
        if(this.state.temp===''){
            this.setState({
                temp : '2'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '2'
            })
        }
    }
    three(){
        if(this.state.temp===''){
            this.setState({
                temp : '3'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '3'
            })
        }
    }
    dot(){
        if(this.state.temp===''){
            this.setState({
                temp : '.'
            })
        }
        else{
            this.setState({
                temp : this.state.temp + '.'
            })
        }
    }
    mul(){
        if(this.state.Operator===''){
            this.setState({
                Operator : 'X',
                firstOprand : parseFloat(this.state.temp),
                temp : ''
            })
        }
    }
    add(){
        if(this.state.Operator===''){
            this.setState({
                Operator : '+',
                firstOprand : parseFloat(this.state.temp),
                temp : ''
            })
        }
    }
    sub(){
        if(this.state.Operator===''){
            this.setState({
                Operator : '-',
                firstOprand : parseFloat(this.state.temp),
                temp : ''
            })
        }
    }
    divide(){
        if(this.state.Operator===''){
            this.setState({
                Operator : '/',
                firstOprand : parseFloat(this.state.temp),
                temp : ''
            })
        }
    }
    equal(){
        switch(this.state.Operator){
            case '+':
                this.setState({
                    Operator : '',
                    secondOprand : parseFloat(this.state.temp),
                    temp : 5 + this.state.secondOprand
                })
            break

            case 'X':
                break

            case '/':
                break

            case '-':
                break

            default:
                alert('Invalid OPeration ! \n 2 Oprands are required to calculate them')    
            break
            
        }
    }

    render(){
        return(
            <div>
                <p>{this.state.firstOprand}</p>
                <p>{this.state.Operator}</p>
                <p>{this.state.secondOprand}</p>
                <h2>{this.state.temp}</h2>
                <div className="row">
                    <button onClick={()=>this.seven()}>7</button>
                    <button onClick={()=>this.eight()}>8</button>
                    <button onClick={()=>this.nine()}>9</button>
                    <button onClick={()=>this.add()}>+</button>
                </div>
                <div className="row">
                    <button onClick={()=>this.four()}>4</button>
                    <button onClick={()=>this.five()}>5</button>
                    <button onClick={()=>this.six()}>6</button>
                    <button onClick={()=>this.sub()}>-</button>
                </div>
                <div className="row">
                    <button onClick={()=>this.one()}>1</button>
                    <button onClick={()=>this.two()}>2</button>
                    <button onClick={()=>this.three()}>3</button>
                    <button onClick={()=>this.mul()}>x</button>
                </div>
                <div className="row">
                    <button onClick={()=>this.dot()}>.</button>
                    <button onClick={()=>this.zero()}>0</button>
                    <button onClick={()=>this.equal()}>=</button>
                    <button onClick={()=>this.divide()}>/</button>
                </div>
            </div>
        )
    }
}

export default Calci
